function fnames = mdm_dir2fnames(dir_path)% function fnames = mdm_dir2fnames(dir_path)% DirList = dir(dir_path);[DirSize1,DirSize2] = size(DirList);fnames = {};DirCount = 0;for ndir = 1:DirSize1    fnamstest = getfield(DirList,{ndir,1},'name');    if strcmp(fnamstest(1),'.') == 0        if ~getfield(DirList,{ndir,1},'isdir')            DirCount = DirCount+1;            fnames{DirCount} = fnamstest;        end    endend